package org.firstinspires.ftc.teamcode.subsystems;

import com.seattlesolvers.solverslib.subsystems.SubsystemBase;
import com.qualcomm.robotcore.hardware.DcMotor;
import com.qualcomm.robotcore.hardware.HardwareMap;

public class DriveSubsystem extends SubsystemBase {
    private final DcMotor leftMotor;
    private final DcMotor rightMotor;

    public DriveSubsystem(HardwareMap hardwareMap) {
        // Initialize hardware
        leftMotor = hardwareMap.get(DcMotor.class, "leftMotor");
        rightMotor = hardwareMap.get(DcMotor.class, "rightMotor");

        // Configure motors
        leftMotor.setDirection(DcMotor.Direction.FORWARD);
        rightMotor.setDirection(DcMotor.Direction.REVERSE);
    }

    public void drive(double leftPower, double rightPower) {
        leftMotor.setPower(leftPower);
        rightMotor.setPower(rightPower);
    }

    public void stop() {
        leftMotor.setPower(0);
        rightMotor.setPower(0);
    }

    public void setZeroPowerBehavior(DcMotor.ZeroPowerBehavior behavior) {
        leftMotor.setZeroPowerBehavior(behavior);
        rightMotor.setZeroPowerBehavior(behavior);
    }
}

// POOR: Commands directly access hardware
public class BadDriveCommand extends CommandBase {
    private DcMotor leftMotor;
    private DcMotor rightMotor;

    public BadDriveCommand(HardwareMap hardwareMap) {
        leftMotor = hardwareMap.get(DcMotor.class, "leftMotor");
        rightMotor = hardwareMap.get(DcMotor.class, "rightMotor");
    }

    @Override
    public void execute() {
        leftMotor.setPower(0.5);
        rightMotor.setPower(0.5);
    }
}

// GOOD: Commands use subsystem abstraction
public class GoodDriveCommand extends CommandBase {
    private final DriveSubsystem driveSubsystem;

    public GoodDriveCommand(DriveSubsystem driveSubsystem) {
        this.driveSubsystem = driveSubsystem;
        addRequirements(driveSubsystem);
    }

    @Override
    public void execute() {
        driveSubsystem.drive(0.5, 0.5);
    }
}

public class ArmSubsystem extends SubsystemBase {
    private final Servo armServo;
    private final DcMotor armMotor;

    // Define possible states for the arm
    public enum ArmState { RETRACTED, EXTENDING, EXTENDED, RETRACTING, HOLDING }
    private ArmState currentState = ArmState.RETRACTED;

    public ArmSubsystem(HardwareMap hardwareMap) {
        armServo = hardwareMap.get(Servo.class, "armServo");
        armMotor = hardwareMap.get(DcMotor.class, "armMotor");
    }

    public void setState(ArmState newState) {
        currentState = newState;
    }

    public ArmState getState() {
        return currentState;
    }

    // Call this method periodically (e.g., in periodic() or from a command)
    public void update() {
        switch (currentState) {
            case RETRACTED:
                armMotor.setPower(0);
                armServo.setPosition(0.0);
                break;
            case EXTENDING:
                armMotor.setPower(0.5);
                // Add logic to check if fully extended, then:
                // currentState = ArmState.EXTENDED;
                break;
            case EXTENDED:
                armMotor.setPower(0);
                armServo.setPosition(1.0);
                break;
            case RETRACTING:
                armMotor.setPower(-0.5);
                // Add logic to check if fully retracted, then:
                // currentState = ArmState.RETRACTED;
                break;
            case HOLDING:
                armMotor.setPower(0.1); // Hold position
                break;
        }
    }
}
// Usage: In your command or opmode, call armSubsystem.setState(ArmState.EXTENDING) and periodically call armSubsystem.update().

public class IntakeSubsystem extends SubsystemBase {
    private final DcMotor intakeMotor;
    private final ShooterSubsystem shooterSubsystem;

    public IntakeSubsystem(HardwareMap hardwareMap, ShooterSubsystem shooterSubsystem) {
        intakeMotor = hardwareMap.get(DcMotor.class, "intakeMotor");
        this.shooterSubsystem = shooterSubsystem;
    }

    public void feedToShooter() {
        if (shooterSubsystem.isReady()) {
            intakeMotor.setPower(0.8);
        } else {
            // Don't feed if shooter isn't ready
            intakeMotor.setPower(0);
        }
    }

    public void stop() {
        intakeMotor.setPower(0);
    }
}

public class ShooterSubsystem extends SubsystemBase {
    private final DcMotor shooterMotor;
    private boolean isReady;

    public ShooterSubsystem(HardwareMap hardwareMap) {
        shooterMotor = hardwareMap.get(DcMotor.class, "shooterMotor");
        isReady = false;
    }

    public void spinUp() {
        shooterMotor.setPower(1.0);
        // Simulate spin-up time
        new Timer().schedule(new TimerTask() {
            @Override
            public void run() {
                isReady = true;
            }
        }, 2000);
    }

    public boolean isReady() {
        return isReady;
    }
}

