// Define the ElevatorSubsystem class
public class ElevatorSubsystem extends SubsystemBase {
    private final DcMotor elevatorMotor;
    private final int minPosition = 0;
    private final int maxPosition = 5000;

    public ElevatorSubsystem(HardwareMap hardwareMap) {
        elevatorMotor = hardwareMap.get(DcMotor.class, "elevatorMotor");
        elevatorMotor.setMode(DcMotor.RunMode.STOP_AND_RESET_ENCODER);
        elevatorMotor.setMode(DcMotor.RunMode.RUN_TO_POSITION);
    }
    // Add methods for moving, stopping, and state tracking below
}

// Add movement methods to the subsystem
public void moveToPosition(int targetTicks, double power) {
    elevatorMotor.setTargetPosition(targetTicks);
    elevatorMotor.setMode(DcMotor.RunMode.RUN_TO_POSITION);
    elevatorMotor.setPower(power);
}
public void stop() {
    elevatorMotor.setPower(0);
}

// Add state tracking fields and methods
private boolean isMoving = false;
public boolean isMoving() {
    return elevatorMotor.isBusy();
}
public int getCurrentPosition() {
    return elevatorMotor.getCurrentPosition();
}
// Update isMoving in your moveToPosition/stop methods
public void moveToPosition(int targetTicks, double power) {
    elevatorMotor.setTargetPosition(targetTicks);
    elevatorMotor.setMode(DcMotor.RunMode.RUN_TO_POSITION);
    elevatorMotor.setPower(power);
    isMoving = true;
}
public void stop() {
    elevatorMotor.setPower(0);
    isMoving = false;
}
