// Initialize motor in runOpMode()
DcMotor testMotor = hardwareMap.get(DcMotor.class, "test_motor");

// Drive motor with constant power
while (opModeIsActive()) {
    testMotor.setPower(0.5); // 50% power
}

// Add telemetry to show motor power
while (opModeIsActive()) {
    telemetry.addData("Motor Power", testMotor.getPower());
    telemetry.update();
    testMotor.setPower(0.5);
}

// Initialize servo
Servo testServo = hardwareMap.get(Servo.class, "test_servo");

// Control servo with gamepad
while (opModeIsActive()) {
    if (gamepad1.a) {
        testServo.setPosition(0.0); // Close position
    } else if (gamepad1.b) {
        testServo.setPosition(1.0); // Open position
    }
}

