import com.qualcomm.robotcore.eventloop.opmode.LinearOpMode;
import com.qualcomm.robotcore.eventloop.opmode.TeleOp;
import com.qualcomm.robotcore.hardware.DcMotor;
import com.qualcomm.robotcore.hardware.Servo;
import com.qualcomm.robotcore.hardware.ColorSensor;


@TeleOp(name = "PracticeOpMode")
public class PracticeOpMode extends LinearOpMode {
    private DcMotor testMotor;
    private Servo testServo;
    private ColorSensor colorSensor;

    @Override
    public void runOpMode() {
        // Initialize all hardware components
        testMotor = hardwareMap.get(DcMotor.class, "test_motor");
        testServo = hardwareMap.get(Servo.class, "test_servo");
        colorSensor = hardwareMap.get(ColorSensor.class, "color_sensor");

        // Wait for start
        waitForStart();

        while (opModeIsActive()) {
            // Drive motor with left stick
            double motorPower = -gamepad1.left_stick_y;
            testMotor.setPower(motorPower);

            // Control servo with buttons
            if (gamepad1.a) {
                testServo.setPosition(0.0);
            } else if (gamepad1.b) {
                testServo.setPosition(1.0);
            }

            // Display telemetry
            telemetry.addData("Motor Power", motorPower);
            telemetry.addData("Servo Position", testServo.getPosition());
            telemetry.addData("Color Sensor Red", colorSensor.red());
            telemetry.update();
        }
    }
}
