// AFTER: Well-structured program using best practices
public class GoodStructure {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        // [GOOD] Variables declared before loop with proper scope
        int total = 0;
        int numberCount = 0;
        int errorCount = 0;

        System.out.println("=== Number Accumulator ===");
        System.out.println("Enter numbers to add (type 'quit' to finish)");

        // [GOOD] Clean loop structure
        while (true) {
            System.out.print("Enter number (or 'quit'): ");
            String input = scanner.nextLine().trim();

            // [GOOD] Exit condition checked first
            if (input.equalsIgnoreCase("quit")) {
                break;
            }

            // [GOOD] Input validation with continue
            try {
                int number = Integer.parseInt(input);

                // [GOOD] Process valid input
                total += number;
                numberCount++;
                System.out.println("Added " + number + " (running total: " + total + ")");

            } catch (NumberFormatException e) {
                // [GOOD] Handle errors gracefully
                System.out.println("[BAD] Invalid number: " + input);
                errorCount++;
                continue;
            }
        }

        // [GOOD] All final processing after loop
        System.out.println("\n=== Final Results ===");
        System.out.println("Total sum: " + total);
        System.out.println("Numbers entered: " + numberCount);
        System.out.println("Errors encountered: " + errorCount);

        if (numberCount > 0) {
            double average = (double) total / numberCount;
            System.out.println("Average: " + String.format("%.2f", average));

            // Additional analysis
            double successRate = (double) numberCount / (numberCount + errorCount) * 100;
            System.out.println("Success rate: " + String.format("%.1f", successRate) + "%");
        } else {
            System.out.println("No valid numbers were entered.");
        }

        System.out.println("Program completed successfully.");
    }
}

import java.util.Scanner;
import java.util.ArrayList;
import java.util.List;

public class DataCollectionSystem {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);

        // [GOOD] All variables declared with proper scope BEFORE loops
        List<Double> sensorData = new ArrayList<>();
        double sum = 0.0;
        double min = Double.MAX_VALUE;
        double max = Double.MIN_VALUE;
        int validReadings = 0;
        int invalidReadings = 0;
        int totalAttempts = 0;

        // Configuration constants
        final double MIN_VALID = 0.0;
        final double MAX_VALID = 100.0;
        final int TARGET_READINGS = 10;

        System.out.println("=== Robot Sensor Data Collection System ===");
        System.out.println("Target readings: " + TARGET_READINGS);
        System.out.println("Valid range: " + MIN_VALID + " - " + MAX_VALID);
        System.out.println("Enter sensor readings (type 'done' when finished)\n");

        // [GOOD] Main data collection loop with clean structure
        while (validReadings < TARGET_READINGS) {
            System.out.printf("Reading %d/%d: ", validReadings + 1, TARGET_READINGS);
            String input = scanner.nextLine().trim();
            totalAttempts++;

            // [GOOD] Early exit condition
            if (input.equalsIgnoreCase("done")) {
                System.out.println("Data collection stopped by user.");
                break;
            }

            // [GOOD] Input validation with proper error handling
            double reading;
            try {
                reading = Double.parseDouble(input);
            } catch (NumberFormatException e) {
                System.out.println("[BAD] Invalid format. Please enter a decimal number.");
                invalidReadings++;
                continue;
            }

            // [GOOD] Range validation
            if (reading < MIN_VALID || reading > MAX_VALID) {
                System.out.printf("[BAD] Out of range (%.2f). Must be %.1f-%.1f\n", 
                                reading, MIN_VALID, MAX_VALID);
                invalidReadings++;
                continue;
            }

            // [GOOD] Process valid reading
            sensorData.add(reading);
            sum += reading;
            validReadings++;

            // Update min/max
            if (reading < min) min = reading;
            if (reading > max) max = reading;

            System.out.printf("[GOOD] Valid reading: %.2f\n", reading);

            // Show progress
            if (validReadings < TARGET_READINGS) {
                double currentAvg = sum / validReadings;
                System.out.printf("   Current average: %.2f, Range: %.2f-%.2f\n", 
                                currentAvg, min, max);
            }
        }

        // [GOOD] ALL analysis and output AFTER the loop
        System.out.println("\n=== Data Collection Complete ===");
        System.out.println("Valid readings collected: " + validReadings);
        System.out.println("Invalid attempts: " + invalidReadings);
        System.out.println("Total attempts: " + totalAttempts);

        if (validReadings > 0) {
            // Basic statistics
            double average = sum / validReadings;
            double range = max - min;

            System.out.println("\nüìä Statistical Analysis:");
            System.out.printf("Average: %.2f\n", average);
            System.out.printf("Minimum: %.2f\n", min);
            System.out.printf("Maximum: %.2f\n", max);
            System.out.printf("Range: %.2f\n", range);
            System.out.printf("Sum: %.2f\n", sum);

            // Calculate standard deviation
            double variance = 0.0;
            for (double value : sensorData) {
                double diff = value - average;
                variance += diff * diff;
            }
            variance /= validReadings;
            double stdDev = Math.sqrt(variance);
            System.out.printf("Standard deviation: %.2f\n", stdDev);

            // Data quality assessment
            double errorRate = (double) invalidReadings / totalAttempts * 100;
            System.out.println("\nüîç Data Quality Assessment:");
            System.out.printf("Error rate: %.1f%%\n", errorRate);
            System.out.printf("Data completeness: %.1f%%\n", 
                            (double) validReadings / TARGET_READINGS * 100);

            if (errorRate < 5 && stdDev < 10) {
                System.out.println("[GOOD] EXCELLENT: High quality data collected");
            } else if (errorRate < 15 && stdDev < 20) {
                System.out.println("[WARNING] GOOD: Acceptable data quality");
            } else {
                System.out.println("[BAD] POOR: Data quality issues detected");
            }

            // Display all readings
            System.out.println("\nüìã All Valid Readings:");
            for (int i = 0; i < sensorData.size(); i++) {
                System.out.printf("  %2d: %6.2f\n", i + 1, sensorData.get(i));
            }

        } else {
            System.out.println("[BAD] No valid data collected.");
        }

        System.out.println("\nData collection system shutdown.");
    }
}
