public class FRCSensor {
    private String sensorType;
    private String sensorId;
    private double lastReading;
    private boolean isConnected;

    public FRCSensor(String type, String id) {
        this.sensorType = type;
        this.sensorId = id;
        this.lastReading = 0.0;
        this.isConnected = true;
    }

    @Override
    public boolean equals(Object compared) {
        // Same object reference
        if (this == compared) {
            return true;
        }

        // Null check
        if (compared == null) {
            return false;
        }

        // Type check
        if (!(compared instanceof FRCSensor)) {
            return false;
        }

        // Cast and compare important properties
        FRCSensor other = (FRCSensor) compared;

        // Two sensors are equal if they have same type and ID
        return this.sensorType.equals(other.sensorType) && 
               this.sensorId.equals(other.sensorId);
    }

    public void updateReading(double reading) {
        this.lastReading = reading;
    }

    public double getLastReading() {
        return this.lastReading;
    }

    public String getSensorType() {
        return this.sensorType;
    }

    public String getSensorId() {
        return this.sensorId;
    }

    public boolean isConnected() {
        return this.isConnected;
    }

    public void setConnected(boolean connected) {
        this.isConnected = connected;
    }

    @Override
    public String toString() {
        return sensorType + " [" + sensorId + "] - " + 
               String.format("%.2f", lastReading) + 
               (isConnected ? " (Connected)" : " (Disconnected)");
    }
}

// Usage example:
FRCSensor ultrasonic1 = new FRCSensor("Ultrasonic", "US-001");
FRCSensor ultrasonic2 = new FRCSensor("Ultrasonic", "US-001");  // Same type and ID
FRCSensor gyro1 = new FRCSensor("Gyroscope", "GY-001");

// Test equality
System.out.println("ultrasonic1.equals(ultrasonic2): " + ultrasonic1.equals(ultrasonic2));  // true
System.out.println("ultrasonic1.equals(gyro1): " + ultrasonic1.equals(gyro1));              // false
System.out.println("ultrasonic1 == ultrasonic2: " + (ultrasonic1 == ultrasonic2));         // false

// Test with different readings (should still be equal)
ultrasonic1.updateReading(25.5);
ultrasonic2.updateReading(30.2);
System.out.println("After different readings:");
System.out.println("Still equal? " + ultrasonic1.equals(ultrasonic2));  // true
System.out.println(ultrasonic1);
System.out.println(ultrasonic2);

import java.util.ArrayList;

public class FRCRobotAdvanced {
    private String teamName;
    private int teamNumber;
    private ArrayList<FRCSensor> sensors;
    private double batteryLevel;

    public FRCRobotAdvanced(String name, int number) {
        this.teamName = name;
        this.teamNumber = number;
        this.sensors = new ArrayList<>();
        this.batteryLevel = 100.0;
    }

    // Safe method to add a sensor
    public void addSensor(FRCSensor sensor) {
        if (sensor != null && !sensors.contains(sensor)) {
            sensors.add(sensor);
            System.out.println("[OK] Added sensor: " + sensor.getSensorType() + " [" + sensor.getSensorId() + "]");
        } else if (sensor == null) {
            System.out.println("[ERROR] Cannot add null sensor");
        } else {
            System.out.println("[WARNING]  Sensor already exists: " + sensor.getSensorType() + " [" + sensor.getSensorId() + "]");
        }
    }

    // Safe method to get a sensor by type and ID
    public FRCSensor getSensor(String type, String id) {
        for (FRCSensor sensor : sensors) {
            if (sensor != null && sensor.getSensorType().equals(type) && sensor.getSensorId().equals(id)) {
                return sensor;
            }
        }
        return null;  // Not found
    }

    // Clone method that creates a deep copy
    public FRCRobotAdvanced clone() {
        System.out.println("[ROBOT] Cloning robot: " + this.teamName);

        // Create new robot with same basic properties
        FRCRobotAdvanced clone = new FRCRobotAdvanced(this.teamName + " (Clone)", this.teamNumber);
        clone.batteryLevel = this.batteryLevel;

        // Deep copy all sensors
        for (FRCSensor sensor : this.sensors) {
            if (sensor != null) {
                // Create new sensor with same properties
                FRCSensor clonedSensor = new FRCSensor(sensor.getSensorType(), sensor.getSensorId() + "-CLONE");
                clonedSensor.updateReading(sensor.getLastReading());
                clonedSensor.setConnected(sensor.isConnected());
                clone.addSensor(clonedSensor);
            }
        }

        return clone;
    }

    // Safe method to use battery
    public void useBattery(double amount) {
        if (amount > 0) {
            this.batteryLevel = Math.max(0, this.batteryLevel - amount);
        }
    }

    // Safe method to get sensor count
    public int getSensorCount() {
        return sensors.size();
    }

    // Safe method to get all sensors (returns copy to prevent external modification)
    public ArrayList<FRCSensor> getAllSensors() {
        ArrayList<FRCSensor> sensorCopy = new ArrayList<>();
        for (FRCSensor sensor : sensors) {
            if (sensor != null) {
                sensorCopy.add(sensor);
            }
        }
        return sensorCopy;
    }

    @Override
    public String toString() {
        return teamName + " (Team " + teamNumber + ") - Battery: " + 
               String.format("%.1f", batteryLevel) + "%, Sensors: " + sensors.size();
    }
}

// Usage example:
FRCRobotAdvanced originalRobot = new FRCRobotAdvanced("Storm Surge", 98765);

// Add sensors safely
originalRobot.addSensor(new FRCSensor("Ultrasonic", "US-001"));
originalRobot.addSensor(new FRCSensor("Gyroscope", "GY-001"));
originalRobot.addSensor(null);  // Test null handling

// Use some battery
originalRobot.useBattery(25.0);

System.out.println("\nOriginal robot: " + originalRobot);

// Clone the robot
FRCRobotAdvanced clonedRobot = originalRobot.clone();
System.out.println("Cloned robot: " + clonedRobot);

// Modify original robot
originalRobot.useBattery(30.0);
originalRobot.addSensor(new FRCSensor("Color", "CO-001"));

System.out.println("\nAfter modifications:");
System.out.println("Original: " + originalRobot);
System.out.println("Clone: " + clonedRobot);
System.out.println("Are they the same object? " + (originalRobot == clonedRobot));
