public class Servo {
    private String servoName;
    private double currentPosition;
    private double minPosition;
    private double maxPosition;
    private boolean isReversed;

    // Constructor 1: Just name (use standard servo range)
    public Servo(String name) {
        this(name, 0.0, 1.0);  // Standard servo range
    }

    // Constructor 2: Name and custom range
    public Servo(String name, double minPos, double maxPos) {
        this(name, minPos, maxPos, false);  // Not reversed
    }

    // Constructor 3: Full configuration
    public Servo(String name, double minPos, double maxPos, boolean reversed) {
        this.servoName = name;
        this.minPosition = minPos;
        this.maxPosition = maxPos;
        this.isReversed = reversed;
        this.currentPosition = minPos;  // Start at minimum position

        System.out.println("Servo " + name + " initialized:");
        System.out.println("  Range: " + minPos + " to " + maxPos);
        System.out.println("  Reversed: " + reversed);
    }

    // Overloaded position methods
    public void setPosition(double position) {
        this.setPosition(position, true);  // Validate by default
    }

    public void setPosition(double position, boolean validate) {
        if (validate && (position < minPosition || position > maxPosition)) {
            System.out.println("[ERROR] Position " + position + " out of range for " + servoName);
            return;
        }

        double actualPosition = isReversed ? (maxPosition - position + minPosition) : position;
        this.currentPosition = actualPosition;

        System.out.println(servoName + " moved to position: " + actualPosition);
    }

    public String toString() {
        return servoName + " at position " + String.format("%.2f", currentPosition) + 
               " (range: " + minPosition + " to " + maxPosition + ", reversed: " + isReversed + ")";
    }
}

// Usage examples:
Servo claw = new Servo("Claw");                          // Standard range
Servo arm = new Servo("Arm", 0.1, 0.9);                  // Custom range
Servo wrist = new Servo("Wrist", 0.0, 0.8, true);        // Reversed

claw.setPosition(0.5);
arm.setPosition(0.7);
wrist.setPosition(0.3);

System.out.println(claw);
System.out.println(arm);
System.out.println(wrist);

public class FRCSensor {
    private String sensorType;
    private double lastReading;
    private int totalReadings;

    public FRCSensor(String type) {
        this.sensorType = type;
        this.lastReading = 0.0;
        this.totalReadings = 0;
    }

    // Single reading
    public double readSensor() {
        return this.readSensor(1);
    }

    // Multiple readings (averaged)
    public double readSensor(int numReadings) {
        return this.readSensor(numReadings, 50);
    }

    // Multiple readings with delay
    public double readSensor(int numReadings, int delayMs) {
        return this.readSensor(numReadings, delayMs, true);
    }

    // Full control over sampling
    public double readSensor(int numReadings, int delayMs, boolean showProgress) {
        if (showProgress) {
            System.out.printf("Reading %s sensor %d times with %dms delay...\n", 
                             sensorType, numReadings, delayMs);
        }

        double total = 0.0;
        for (int i = 0; i < numReadings; i++) {
            // Simulate sensor reading with some noise
            double reading = 50 + (Math.random() - 0.5) * 20;  // 40-60 range
            total += reading;

            if (showProgress && numReadings > 1) {
                System.out.printf("  Reading %d: %.2f\n", i + 1, reading);
            }

            // Simulate delay
            if (i < numReadings - 1 && delayMs > 0) {
                try {
                    Thread.sleep(delayMs);
                } catch (InterruptedException e) {
                    break;
                }
            }
        }

        double average = total / numReadings;
        this.lastReading = average;
        this.totalReadings += numReadings;

        if (showProgress && numReadings > 1) {
            System.out.printf("Average: %.2f\n", average);
        }

        return average;
    }

    // Filtered reading (remove outliers)
    public double readSensorFiltered() {
        return this.readSensorFiltered(5, 100);
    }

    public double readSensorFiltered(int samples, int delayMs) {
        double[] readings = new double[samples];

        System.out.printf("Taking %d filtered readings from %s sensor...\n", samples, sensorType);

        for (int i = 0; i < samples; i++) {
            readings[i] = 50 + (Math.random() - 0.5) * 20;
            if (delayMs > 0) {
                try {
                    Thread.sleep(delayMs);
                } catch (InterruptedException e) {
                    break;
                }
            }
        }

        // Simple filtering: remove highest and lowest values
        double min = readings[0];
        double max = readings[0];
        double sum = 0;

        for (double reading : readings) {
            sum += reading;
            if (reading < min) min = reading;
            if (reading > max) max = reading;
        }

        // Average without extremes
        double filtered = (sum - min - max) / (samples - 2);
        this.lastReading = filtered;
        this.totalReadings += samples;

        System.out.printf("Filtered result: %.2f (removed %.2f and %.2f)\n", filtered, min, max);
        return filtered;
    }

    public double getLastReading() {
        return this.lastReading;
    }

    public int getTotalReadings() {
        return this.totalReadings;
    }
}

// Usage examples:
FRCSensor ultrasonicSensor = new FRCSensor("Ultrasonic");

// Different ways to read the sensor
double reading1 = ultrasonicSensor.readSensor();           // Single reading
double reading2 = ultrasonicSensor.readSensor(3);          // 3 readings averaged
double reading3 = ultrasonicSensor.readSensor(5, 100);     // 5 readings, 100ms apart
double reading4 = ultrasonicSensor.readSensor(3, 50, false); // Silent mode
double reading5 = ultrasonicSensor.readSensorFiltered();   // Filtered reading
double reading6 = ultrasonicSensor.readSensorFiltered(7, 75); // Custom filtered

System.out.printf("\nLast reading: %.2f, Total readings taken: %d\n", 
                 ultrasonicSensor.getLastReading(), ultrasonicSensor.getTotalReadings());
