public class BatteryStatusChecker {
    public static void main(String[] args) {
        double batteryVoltage = 11.8;
        int batteryPercentage = 72;

        System.out.println("=== Battery Status Check ===");
        System.out.println("Voltage: " + batteryVoltage + "V");
        System.out.println("Percentage: " + batteryPercentage + "%");

        // Multiple condition checking
        if (batteryVoltage >= 12.5 && batteryPercentage >= 90) {
            System.out.println("[GOOD] Battery Status: EXCELLENT");
            System.out.println("[OK] Ready for competition");
        } else if (batteryVoltage >= 12.0 && batteryPercentage >= 70) {
            System.out.println("[MODERATE] Battery Status: GOOD");
            System.out.println("[OK] Safe for normal operation");
        } else if (batteryVoltage >= 11.5 && batteryPercentage >= 50) {
            System.out.println("[MODERATE] Battery Status: MODERATE");
            System.out.println("[WARNING] Consider charging soon");
        } else if (batteryVoltage >= 11.0 && batteryPercentage >= 25) {
            System.out.println("üü† Battery Status: LOW");
            System.out.println("[WARNING] Charge immediately after match");
        } else {
            System.out.println("[RED] Battery Status: CRITICAL");
            System.out.println("üõë DO NOT OPERATE - CHARGE NOW");
        }
    }
}

public class SensorValidator {
    public static void main(String[] args) {
        // Simulate different sensor readings
        int colorSensorValue = 150;
        double distanceSensorValue = 25.7;
        int touchSensorValue = 1;

        System.out.println("=== Sensor Validation System ===");

        // Color sensor validation (0-255 range)
        if (colorSensorValue >= 0 && colorSensorValue <= 255) {
            if (colorSensorValue > 200) {
                System.out.println("[GOOD] Color Sensor: Bright light detected");
            } else if (colorSensorValue > 100) {
                System.out.println("[MODERATE] Color Sensor: Medium light detected");
            } else {
                System.out.println("[LOW] Color Sensor: Low light detected");
            }
        } else {
            System.out.println("[ERROR] Color Sensor: Invalid reading (" + colorSensorValue + ")");
        }

        // Distance sensor validation (0-100 cm range)
        if (distanceSensorValue >= 0.0 && distanceSensorValue <= 100.0) {
            if (distanceSensorValue <= 5.0) {
                System.out.println("[RED] Distance Sensor: OBSTACLE VERY CLOSE (" + distanceSensorValue + "cm)");
            } else if (distanceSensorValue <= 15.0) {
                System.out.println("üü† Distance Sensor: Obstacle close (" + distanceSensorValue + "cm)");
            } else {
                System.out.println("[GOOD] Distance Sensor: Clear path (" + distanceSensorValue + "cm)");
            }
        } else {
            System.out.println("[ERROR] Distance Sensor: Out of range (" + distanceSensorValue + "cm)");
        }

        // Touch sensor validation (0 or 1)
        if (touchSensorValue == 0 || touchSensorValue == 1) {
            if (touchSensorValue == 1) {
                System.out.println("[RED] Touch Sensor: PRESSED - Emergency stop!");
            } else {
                System.out.println("[GOOD] Touch Sensor: Not pressed - Normal operation");
            }
        } else {
            System.out.println("[ERROR] Touch Sensor: Invalid state (" + touchSensorValue + ")");
        }
    }
}

public class GameModeSelector {
    public static void main(String[] args) {
        String currentMode = "autonomous";
        String allianceColor = "blue";
        int matchTime = 125; // seconds remaining

        System.out.println("=== FRC Game Mode System ===");
        System.out.println("Current Mode: " + currentMode);
        System.out.println("Alliance: " + allianceColor);
        System.out.println("Time Remaining: " + matchTime + "s");

        // Game mode logic with string comparison
        if (currentMode.equals("autonomous")) {
            System.out.println("[AUTO] AUTONOMOUS PERIOD");

            if (allianceColor.equalsIgnoreCase("red")) {
                System.out.println("[RED] Red Alliance Strategy Active");
                System.out.println("   ‚Üí Target red alliance scoring positions");
            } else if (allianceColor.equalsIgnoreCase("blue")) {
                System.out.println("[BLUE] Blue Alliance Strategy Active");
                System.out.println("   ‚Üí Target blue alliance scoring positions");
            } else {
                System.out.println("[ERROR] Unknown alliance color: " + allianceColor);
            }

        } else if (currentMode.equals("teleop")) {
            System.out.println("[TELEOP] TELEOPERATED PERIOD");
            System.out.println("   ‚Üí Driver control enabled");
            System.out.println("   ‚Üí Manual scoring mode");

            if (matchTime <= 30) {
                System.out.println("‚è∞ ENDGAME PERIOD - Final scoring opportunities!");
            }

        } else if (currentMode.equals("endgame")) {
            System.out.println("üèÅ ENDGAME PERIOD");
            System.out.println("   ‚Üí Focus on hanging/parking");
            System.out.println("   ‚Üí Maximum point opportunities");

        } else if (currentMode.equals("disabled")) {
            System.out.println("‚è∏Ô∏è ROBOT DISABLED");
            System.out.println("   ‚Üí All motors stopped");
            System.out.println("   ‚Üí Waiting for next period");

        } else {
            System.out.println("[ERROR] UNKNOWN MODE: " + currentMode);
            System.out.println("   ‚Üí Defaulting to safe mode");
        }
    }
}

public class RobotSafetySystem {
    public static void main(String[] args) {
        // System status variables
        boolean emergencyStopPressed = false;
        double motorTemperature = 68.5;
        int batteryLevel = 45;
        boolean communicationActive = true;
        boolean sensorsResponding = true;
        double robotSpeed = 0.75; // 0.0 to 1.0 scale
        String operatorPresent = "yes";

        System.out.println("=== Robot Safety System Check ===");

        // Critical safety checks using logical operators
        boolean criticalFailure = emergencyStopPressed || 
                                 motorTemperature > 85 || 
                                 batteryLevel < 10 || 
                                 !communicationActive;

        boolean warningCondition = (batteryLevel < 25 && robotSpeed > 0.5) ||
                                  (motorTemperature > 75 && robotSpeed > 0.8) ||
                                  !sensorsResponding;

        boolean safeToOperate = !emergencyStopPressed && 
                               motorTemperature < 80 && 
                               batteryLevel > 15 && 
                               communicationActive && 
                               sensorsResponding &&
                               operatorPresent.equalsIgnoreCase("yes");

        // Safety decision logic
        if (criticalFailure) {
            System.out.println("üö® CRITICAL FAILURE - IMMEDIATE SHUTDOWN");

            if (emergencyStopPressed) {
                System.out.println("   üõë Emergency stop activated");
            }
            if (motorTemperature > 85) {
                System.out.println("   üå°Ô∏è Motor overheating: " + motorTemperature + "¬∞C");
            }
            if (batteryLevel < 10) {
                System.out.println("   üîã Critical battery level: " + batteryLevel + "%");
            }
            if (!communicationActive) {
                System.out.println("   üì° Communication failure");
            }

        } else if (warningCondition) {
            System.out.println("[WARNING] WARNING CONDITIONS DETECTED");

            if (batteryLevel < 25 && robotSpeed > 0.5) {
                System.out.println("   üîã Low battery with high speed operation");
            }
            if (motorTemperature > 75 && robotSpeed > 0.8) {
                System.out.println("   üå°Ô∏è High temperature with maximum speed");
            }
            if (!sensorsResponding) {
                System.out.println("   üìä Sensor communication issues");
            }

            System.out.println("   ‚Üí Reducing robot performance for safety");

        } else if (safeToOperate) {
            System.out.println("[OK] ALL SYSTEMS NORMAL");
            System.out.println("   [GOOD] Robot cleared for full operation");

            // Additional operational checks
            if (batteryLevel > 80 && motorTemperature < 60) {
                System.out.println("   ‚≠ê Optimal operating conditions");
            }

        } else {
            System.out.println("‚ùì UNKNOWN SAFETY STATE");
            System.out.println("   ‚Üí Defaulting to safe mode");
        }

        // Status summary
        System.out.println("\n=== System Status Summary ===");
        System.out.println("Emergency Stop: " + (emergencyStopPressed ? "PRESSED" : "Normal"));
        System.out.println("Motor Temp: " + motorTemperature + "¬∞C");
        System.out.println("Battery: " + batteryLevel + "%");
        System.out.println("Communication: " + (communicationActive ? "Active" : "Failed"));
        System.out.println("Sensors: " + (sensorsResponding ? "Responding" : "Error"));
        System.out.println("Operator Present: " + operatorPresent);
    }
}
