public class RobotStatus {
    public static void main(String[] args) {
        // Call method with different parameters
        printRobotStatus("Alpha", 85.5, true);
        printRobotStatus("Beta", 23.2, false);
        printRobotStatus("Gamma", 67.8, true);
    }

    // Method with multiple parameters
    public static void printRobotStatus(String robotName, double batteryLevel, boolean isActive) {
        System.out.println("=== Robot Status Report ===");
        System.out.println("Robot Name: " + robotName);
        System.out.println("Battery Level: " + batteryLevel + "%");
        System.out.println("Status: " + (isActive ? "ACTIVE" : "INACTIVE"));

        // Conditional logic based on parameters
        if (batteryLevel < 25) {
            System.out.println("[WARNING] LOW BATTERY WARNING!");
        } else if (batteryLevel > 80) {
            System.out.println("[OK] Battery level excellent");
        }

        System.out.println("");  // Empty line for spacing
    }
}

/* Output:
=== Robot Status Report ===
Robot Name: Alpha
Battery Level: 85.5%
Status: ACTIVE
[OK] Battery level excellent

=== Robot Status Report ===
Robot Name: Beta
Battery Level: 23.2%
Status: INACTIVE
[WARNING] LOW BATTERY WARNING!

=== Robot Status Report ===
Robot Name: Gamma
Battery Level: 67.8%
Status: ACTIVE

*/

public class CalculationMethods {
    public static void main(String[] args) {
        // Use methods that return values
        double speed1 = calculateSpeed(100, 5);  // 100 inches in 5 seconds
        double speed2 = calculateSpeed(50, 2.5); // 50 inches in 2.5 seconds

        System.out.println("Robot 1 speed: " + speed1 + " inches/second");
        System.out.println("Robot 2 speed: " + speed2 + " inches/second");

        // Use the returned values in other calculations
        double averageSpeed = (speed1 + speed2) / 2;
        System.out.println("Average speed: " + averageSpeed + " inches/second");

        // Multiple method calls in one expression
        double totalDistance = calculateDistance(0, 0, 3, 4) + calculateDistance(3, 4, 6, 8);
        System.out.println("Total distance traveled: " + totalDistance + " units");
    }

    // Method that returns a calculated value
    public static double calculateSpeed(double distance, double time) {
        if (time == 0) {
            System.out.println("Error: Cannot divide by zero time!");
            return 0;
        }
        return distance / time;
    }

    // Another method that returns a value
    public static double calculateDistance(double x1, double y1, double x2, double y2) {
        double deltaX = x2 - x1;
        double deltaY = y2 - y1;
        return Math.sqrt(deltaX * deltaX + deltaY * deltaY);
    }
}

/* Output:
Robot 1 speed: 20.0 inches/second
Robot 2 speed: 20.0 inches/second
Average speed: 20.0 inches/second
Total distance traveled: 10.0 units
*/

public class ComplexRobotProgram {
    public static void main(String[] args) {
        System.out.println("Starting robot competition sequence...");
        runCompetitionSequence();
        System.out.println("Competition sequence complete!");
    }

    // Main coordination method that calls other methods
    public static void runCompetitionSequence() {
        performSystemCheck();

        if (isRobotReady()) {
            executeAutonomousPhase();
            executeTeleopPhase();
        } else {
            System.out.println("[ERROR] Robot not ready - aborting sequence");
        }

        performShutdown();
    }

    public static void performSystemCheck() {
        System.out.println("\n[CHECK] Performing system check...");
        checkBattery();
        checkMotors();
        checkSensors();
        System.out.println("[OK] System check complete");
    }

    public static void checkBattery() {
        System.out.println("  - Battery: OK (87%)");
    }

    public static void checkMotors() {
        System.out.println("  - Motors: All operational");
    }

    public static void checkSensors() {
        System.out.println("  - Sensors: All responding");
    }

    public static boolean isRobotReady() {
        // In a real program, this would check actual robot status
        System.out.println("\nü§ñ Robot status: READY");
        return true;
    }

    public static void executeAutonomousPhase() {
        System.out.println("\nüöÄ Starting autonomous phase...");
        moveToTarget(36, 24);  // Move to coordinates
        deployMechanism();
        returnToStart();
        System.out.println("[OK] Autonomous phase complete");
    }

    public static void executeTeleopPhase() {
        System.out.println("\nüéÆ Starting teleop phase...");
        enableDriverControl();
        System.out.println("[OK] Teleop phase active");
    }

    public static void moveToTarget(double x, double y) {
        double distance = calculateMovementDistance(0, 0, x, y);
        System.out.println("  - Moving " + String.format("%.1f", distance) + " inches to target");
    }

    public static void deployMechanism() {
        System.out.println("  - Deploying scoring mechanism");
    }

    public static void returnToStart() {
        System.out.println("  - Returning to starting position");
    }

    public static void enableDriverControl() {
        System.out.println("  - Driver control enabled");
    }

    public static void performShutdown() {
        System.out.println("\n‚èπÔ∏è Shutting down robot systems...");
        System.out.println("  - Motors stopped");
        System.out.println("  - Sensors disabled");
        System.out.println("[OK] Shutdown complete");
    }

    public static double calculateMovementDistance(double x1, double y1, double x2, double y2) {
        double deltaX = x2 - x1;
        double deltaY = y2 - y1;
        return Math.sqrt(deltaX * deltaX + deltaY * deltaY);
    }
}

